import { withRouter } from 'react-router-dom';
import { Tooltip, Avatar, Dialog, Paper, Popper } from '@material-ui/core';
import { Card, Button, Divider, MuiThemeProvider, createMuiTheme, ClickAwayListener } from '@material-ui/core';
import MenuItem from '@material-ui/core/MenuItem';
import UploadPreview from 'material-ui-upload/UploadPreview';
class Profile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            anchorEl: false,
            dialog: false,
            box: false,
            file:'',
            image: false
        }
    }
    handleSignOut = () => {
        localStorage.removeItem('token')
        this.props.history.push('/')
    }
    handleAccount = () => {
        this.setState({
            dialog: !this.state.dialog,
            anchorEl: !this.state.anchorEl
        })
    }
    handleClick = (e) => {
        this.setState({
            dialog: !this.state.dialog,
            anchorEl: this.state.anchorEl ? !this.state.anchorEl : e.target
        })
    }
    closeDialog = () => {
        this.setState({
            anchorEl: !this.state.anchorEl
        })
    }
    pickImage=(event)=>{
        console.log('in pickimage',event)
