                    <Avatar alt="Remy Sharp"   >{localStorage.getItem('userEmail')}
                    <UploadPreview
              title="Picture"
              label="Add"
              initialItems={this.state.file}
              onChange={this.pickImage}
              />

                    </Avatar>
                </Tooltip>
                {/* <Popper open={this.state.anchorEl} anchorEl={this.state.anchorEl} style={{
