import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Tooltip, Avatar, Dialog } from '@material-ui/core';
import { Card, Button, Divider, MuiThemeProvider, createMuiTheme, ClickAwayListener } from '@material-ui/core';
class Profile extends Component {
    constructor(props) {
        super(props)
        this.state = {
            anchorEl: null,
            dialog : false
        }
    }
    handleSignOut = () => {
        localStorage.removeItem('token')
        this.props.history.push('/')
    }
    handleAccount = () => {
        this.setState({
            anchorEl:  !this.state.anchorEl 
        })
    }
    handleClick = (e) => {
        this.setState({
            anchorEl: this.state.anchorEl ? !this.state.anchorEl : e.target
        })

    }
    closeDialog=()=>{
        this.setState({
            dialog:  !this.state.anchorEl 
        })
    }
    render() {
        return (
            <div>
                <Tooltip title="profile" >
                    <Avatar alt="Remy Sharp" src={this.state.file} onClick={this.handleClick}>{localStorage.getItem('userEmail')}
                    </Avatar>
                </Tooltip>

                <Dialog open={this.state.anchorEl} anchorEl={this.state.anchorEl} >
                <ClickAwayListener onClickAway={this.closeDialog}>
                    <Button variant="contained" onClick={this.handleAccount}>No thanks</Button>
                    <Button variant="contained" onClick={this.handleSignOut}>Sign out</Button>
                    </ClickAwayListener>
                </Dialog>





            </div>
        )
    }
}
export default withRouter(Profile)
