import React, { Component } from 'react';

import { withRouter } from 'react-router-dom';
import { Tooltip } from '@material-ui/core';
import Popper from '@material-ui/core/Popper';
import Paper from '@material-ui/core/Paper';
import DateTimePicker from 'react-datetime-picker';
import 'date-fns';
import DateFnsUtils from "@date-io/date-fns";
import AddAlertOutlinedIcon from '@material-ui/icons/AddAlertOutlined';
import { MuiPickersUtilsProvider } from 'material-ui-pickers';
 class Remainder extends Component {
    constructor(props) {
        super(props);
        this.state = {
             anchorEl: false,
             selectedDate: new Date(),
        }
    }
  handleDateTimePick=()=>{
this.setState({
    anchorEl : !this.state.anchorEl
})
    }
    render() {
        return (
            <div>
                <Tooltip title="Remind me" >
                <AddAlertOutlinedIcon onClick={this.handleDateTimePick}/>
                </Tooltip>
                <Popper open={this.state.anchorEl} anchorEl={this.state.anchorEl} 
                    style={{marginTop: "5px",zIndex:"9999"}}>
                    <Paper className="reminder-paper">
                        <MuiPickersUtilsProvider utils={DateFnsUtils} >
                            <DateTimePicker style={{padding: "5px",width: "175px"}}
                                // value={this.state.selectedDate}
                                // onChange={this.handleChangeDate}
                                // InputProps={{
                                //     disableUnderline: true //underline propose 
                                // }}
                            >
                            </DateTimePicker>
                        </MuiPickersUtilsProvider>
                    </Paper>
                </Popper>

            </div>
        )
    }
}
export default withRouter(Remainder)
