import React, { Component } from 'react';
import { NoteController } from '../../Controller/NoteController';
import { InputBase, Card, TextField } from '@material-ui/core';

import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';

import Dialog from '@material-ui/core/Dialog';

import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import { deleteLabel } from '../../Controller/label';
import More from './more';
import Collaborator from '../Dashboard/collaborator';

import Chip from '@material-ui/core/Chip';
import Remainder from '../Dashboard/remainder';
import Archive from '../Dashboard/archive';
import { updateNote } from '../../Controller/NoteController';
import { withRouter } from 'react-router-dom';
import { Tooltip, ClickAwayListener } from '@material-ui/core';
import Popper from '@material-ui/core/Popper';
import Paper from '@material-ui/core/Paper';
import DateTimePicker from 'react-datetime-picker';
import 'date-fns';
import DateFnsUtils from "@date-io/date-fns";
import AddAlertOutlinedIcon from '@material-ui/icons/AddAlertOutlined';
import { MuiPickersUtilsProvider } from 'material-ui-pickers';
import { setRemainder } from '../../Controller/NoteController';

const themes = createMuiTheme({
    overrides: {
        MuiPaper: {
            rounded: {
                borderRadius: "8px",
                margin:"10px" 
            },
            elevation24: {
                boxShadow: "none"
            }
        },
        MuiBackdrop: {
            root: {
                backgroundColor: "rgba(15,15,15,0.25)"
            }
        }
    }
})
 class AllNotes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            menuItem: false,
            id: '',
            title: '',
            description: '',
            notes: [],
            openDialog: false,
            colabs:[],
            anchorEl: false,
            selectedDate: new Date(),
        }
        console.log(this.props.view)

    }
    handleDateTimePick = (e) => {
        this.setState({
            anchorEl: this.state.anchorEl ? !this.state.anchorEl : e.target
        })
    }
    handleChangeDate = (data) => {
        this.setState({
            selectedDate: data
        })
        console.log('noteId in remainder', this.props.noteId);
        console.log('data', this.state.selectedDate)
        setRemainder(data, this.props.noteId).then((res) => {
            console.log(res.data)
        }).catch((err) => {
            console.log(err)
        })
    }
    handleClickAway = () => {
        this.setState({
            anchorEl: null
        })
    }
    componentDidMount() {
        this.getNotes();
    }
    getNotes = () => {
        NoteController().then((res) => {
            console.log("in getNotes ", res.data);
            this.setState({
                notes: res.data,
            })
            console.log('data', this.state.notes)
            console.log('colab', this.state.colabs)
        }
        )
    }
    updateCard=(note)=>{
        console.log("new note",note);

        var updatedNotes=[...this.state.notes,note];
        this.setState({
            notes:updatedNotes
        })
    }
    handleTitleChange = (event) => {
        this.setState({
            title: event.target.value
        })
    }
    handleDescription = (event) => {
        this.setState({
            description: event.target.value
        })
    }

    handleClickTakeNote = (note) => {
        console.log(note)
        this.setState({
            id: note.id,
            title: note.title,
            description: note.description,
            openDialog: !this.state.openDialog,
        })
    }
    deleteLabel=(label)=>{
console.log(label)
deleteLabel(label.id).then((res)=>{
    console.log(res.data)
}).catch((err)=>{
    console.log(err.data)
})
    }
    pickerOpen=(key)=>{

    }
    closeDialog = () => {
        if (this.state.title === "" && this.state.description === "")
            return this.setState({ openDialog: !this.state.openDialog })
        else {
            var editedNote = {
                "title": this.state.title,
                "description": this.state.description,

            }
            console.log('noteed', editedNote)

            this.setState({

                openDialog: !this.state.openDialog,
            })
            updateNote(editedNote, this.state.id).then((res) => {
                console.log(res.data);
            })
        }
    }

    render() {
        const viewNote=!this.props.view ? "note-display": "fullbox-display"
        const viewFooter= !this.props.view ? "note-display-footer": "fullbox-display-footer"
        let getAllNotes = this.state.notes.map((keys)=>{ 
            return(
                keys=== null ? '' : 
               < div key={keys.id} >
                < Card key={keys.id} className={viewNote} >
                    <div onClick={() => { this.handleClickTakeNote(keys) }}>
                        <CardContent>
                            {keys.title}
                        </CardContent>
                        <CardContent>
                            {keys.description}<br/>
                            <div>{keys.remainder=== null ? '': <Chip  label=  {keys.remainder} onDelete={()=>this.deleteRemainder(keys)} onClick={()=>this.pickerOpen(keys)} variant="outlined"/>  }

                            {keys.labels.map((labela) =>{

                                return ( <div key={labela.id}>{labela === null? '':
                                <Chip  label=  {labela.labelName} onDelete={()=>this.deleteLabel(labela)} variant="outlined"/>}
                                </div> );

                            })}

                            </div>
                        </CardContent>
                    </div>
                    <CardActions  className={viewFooter}>
                        <Remainder noteId={keys.id}/>
                        <Collaborator noteId={keys}/>
                        <Archive note={keys.id}/>
                        <More noteId={keys.id} />
                    </CardActions>
                </Card >
                <Dialog open={this.state.openDialog} >
                    < Card className="note-dialog" style={{ boxShadow: "1px 1px 1px 1px"}} >
                        <CardContent>
                            <TextField
                                type="text"
                                multiline


                                value={this.state.title}
                                onChange={this.handleTitleChange}
                            /></CardContent>
                        <CardContent>
                            <TextField
                                type="text"
                                multiline
                                value={this.state.description}
                                onChange={this.handleDescription}
                            /></CardContent>
                        <CardActions>
                        <Remainder />
                        <Collaborator noteId={keys}/>



                        <Archive note={keys.id}/>
                            <More noteId={keys.id} />
                            <Button className="button-close" style={{float: "right "} } onClick={this.closeDialog}>Close</Button>

                        </CardActions>
                    </Card >
                </Dialog>
              </div >

            )
        })
        return (
            <div>
                <Tooltip title="Remind me" >
                    <AddAlertOutlinedIcon onClick={(e) => { this.handleDateTimePick(e) }} />
                </Tooltip>
                <Popper open={this.state.anchorEl} anchorEl={this.state.anchorEl}
                    style={{ marginTop: "5px", zIndex: "9999" }}>
                    <ClickAwayListener onClickAway={this.handleClickAway}>
                        <Paper className="reminder-paper">
                            <MuiPickersUtilsProvider utils={DateFnsUtils} >
                                <DateTimePicker style={{ padding: "5px", width: "175px" }}
                                    value={this.state.selectedDate}
                                    onChange={this.handleChangeDate}
                                >
                                </DateTimePicker>
                            </MuiPickersUtilsProvider>
                        </Paper>
                    </ClickAwayListener>
                </Popper>
            </div>
        )

        console.log(this.props.view)
        return (
            <div className="allNotePage">
                <MuiThemeProvider theme={themes}>
                    {getAllNotes}
                </MuiThemeProvider>
            </div >
        )
    }
}
export default withRouter(AllNotes)
